import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';


function SearchContainer({ onOrderChange, onFilterChange, onSortChange,onSearchChange }, props) {


    // true ascending false descending
    const handleOrderBy = (value) => {
        onOrderChange(value);
    }

    // genres ???? 15 long ary 
    const handleFilterBy = (value) => {
        onFilterChange(value);
    }

    // name and popularity
    const handleSortBy = (value) => {
        onSortChange(value);
    }

    // search name - string
    const handleSearchQuery = (value) => {
        onSearchChange(value);
    }
    return (
        <>

            <div class="d-flex flex-row justify-content-center p-4">

                <div>
                    <input type="text" placeholder="Search..." onChange={handleSearchQuery} />
                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Filter By (genres)
                    </button>

                    <ul class="dropdown-menu">
                        {
                            props.filterOptions.map((option)=>
                                <li><a class="dropdown-item" >option.name</a></li>
                            )

                        }
                    </ul>
                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Sort By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className="dropdown-item" onClick={() => handleSortBy(true)}>Name</a></li>
                        <li><a className="dropdown-item" onClick={() => handleSortBy(false)}>popularity</a></li>
                    </ul>
                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Order By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className="dropdown-item" onClick={() => handleOrderBy(true)}>Ascending</a></li>
                        <li><a className="dropdown-item" onClick={() => handleOrderBy(false)}>Descending</a></li>
                    </ul>
                </div>



            </div>

        </>
    );
}

export default SearchContainer;
