import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';


function SearchGenres({ onOrderChange, onFilterChange, onSortChange, onSearchChange, filterOptions }) {

    // search name - string
    const handleSearchQuery = (value) => {
        onSearchChange(value);
    }


    // name , eventPricemin, eventPriceMax
    const handleSortBy = (value) => {
        onSortChange(value);
    }

    const [activeButton, setActiveButton] = useState(null);
    // true ascending false descending
    const handleOrderBy = (value) => {
        setActiveButton(value);
        onOrderChange(value);
    }

    return (
        <>

            <div class="d-flex flex-row justify-content-center align-items-center p-4">

                <div class ="me-2">
                    <input type="text" placeholder="Search..." onChange={handleSearchQuery} />
                </div>

                <div class="dropdown me-2">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Filter By (genres)
                    </button>
                    <ul class="dropdown-menu">
                        {
                            filterOptions.map((option, index) =>
                                <li key ={index}><a class="dropdown-item" onClick={() => handleFilterBy(option.genre_id)} >{option.name}</a></li>
                            )
                        }
                    </ul>
                </div>

                <div class="dropdown me-2">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Sort By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className="dropdown-item" onClick={() => handleSortBy(true)}>Name</a></li>
                        <li><a className="dropdown-item" onClick={() => handleSortBy(false)}>popularity</a></li>
                    </ul>
                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Order By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className={`dropdown-item ${activeButton ? 'active' : ''}`} onClick={() => handleOrderBy(true)}>Ascending</a></li>
                        <li><a className={`dropdown-item ${!activeButton && activeButton !==null ? 'active' :'' }`} onClick={() => handleOrderBy(false)}>Descending</a></li>
                    </ul>
                </div>



            </div>

        </>
    );
}

export default SearchGenres;
