import React from 'react';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';


function SearchContainer({ onOrderbyChange, onFilterChange, onSearchChange }) {

    const handleSearchInput = (event) => {
        const { value } = event.target;
        onSearchChange(value);
    };

    const handleOrderbySelection = (value) => {
        onOrderbyChange(value);
    };

    const handleFilterSelection = (value) => {
        onFilterChange(value);
    };




    return (
        <>

            <div class="d-flex flex-row justify-content-center p-4">

                {/* /* search bar */}
                <div>
                    <input type="text" placeholder="Search..." onChange={handleSearchInput} />

                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Genres
                    </button>

                    <ul class="dropdown-menu">
                        <li><a class="dropdown-item" href="#">Action</a></li>
                    </ul>
                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Sort By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className="dropdown-item" onClick={() => setsortBy(true)}>Name</a></li>
                        <li><a className="dropdown-item" onClick={() => setsortBy(false)}>popularity</a></li>
                    </ul>
                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Order By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className="dropdown-item" onClick={() => setOrderby(true)}>Ascending</a></li>
                        <li><a className="dropdown-item" onClick={() => setOrderby(false)}>Descending</a></li>
                    </ul>
                </div>



            </div>

        </>
    );
}

export default SearchContainer;
