import React from 'react';
import Slider from 'react-slider';
import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';


function SearchGenres({ onOrderChange, onSortChange, onValuesChange, onSearchChange }) {

    // search name - string
    const handleSearchQuery = (value) => {
        onSearchChange(value);
    }

    const handleValuesChange = (value) => {
        onValuesChange(value)
    }


    // name , eventPricemin, eventPriceMax
    const handleSortBy = (value) => {
        onSortChange(value);
    }

    const [activeButton, setActiveButton] = useState(null);
    // true ascending false descending
    const handleOrderBy = (value) => {
        setActiveButton(value);
        onOrderChange(value);
    }

    return (
        <>

            <div class="d-flex flex-row justify-content-center align-items-center p-4">

                <div class="me-2">
                    <input type="text" placeholder="Search..." onChange={handleSearchQuery} />
                </div>


                <div>
                    <Slider
                        min={0}
                        max={200} // Adjust max based on your needs
                        step={1}
                        values={values}
                        onChange={handleValuesChange}
                        withBars
                    />
                    <div>
                        <span>Min: {values[0]}</span>
                        <span>Max: {values[1]}</span>
                    </div>
                </div>

                <div class="dropdown me-2">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Sort By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className="dropdown-item" onClick={() => handleSortBy('name')}>Name</a></li>
                        <li><a className="dropdown-item" onClick={() => handleSortBy('eventsPriceMin')}>Events price minimum</a></li>
                        <li><a className="dropdown-item" onClick={() => handleSortBy('eventsPriceMax')}>Events price maximum</a></li>
                    </ul>
                </div>

                <div class="dropdown">
                    <button class="btn btn-secondary dropdown-toggle" href="#" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Order By
                    </button>

                    <ul className="dropdown-menu">
                        <li><a className={`dropdown-item ${activeButton ? 'active' : ''}`} onClick={() => handleOrderBy(true)}>Ascending</a></li>
                        <li><a className={`dropdown-item ${!activeButton && activeButton !== null ? 'active' : ''}`} onClick={() => handleOrderBy(false)}>Descending</a></li>
                    </ul>
                </div>



            </div>

        </>
    );
}

export default SearchGenres;
